import React, { useEffect, useState } from 'react';
import './LoginSignUp.css';

const LoginSignUp = () => {
  const [action, setAction] = useState("Login");
  const [email, setEmail] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const isPasswordValid = () => {
    return password.length > 8 && /\d/.test(password);
  };

  return (
    <div className='container'>
      <div className='header'>
        <div className='text'>{action}</div>
        <div className='underLine'></div>
      </div>
      <div className='inputs'>
        {action === "Login" ? <div></div> : <div className='input'>
          <img src={require("./icon_email.png")} alt="" />
          <input type="email" placeholder='Email Id' value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>}
        <div className='input'>
          <img src={require("./icon_user.png")} alt="" />
          <input type="text" placeholder='UserName' value={username} onChange={(e) => setUsername(e.target.value)} />
        </div>

        <div className='input'>
          <img src={require("./icon_pass.png")} alt="" />
          <input type="text" placeholder='Password' value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
      </div>

      <div className="submit-container">
        <div className={action === "Login" ? "submit gray" : "submit"} onClick={() => { setAction("Sign Up") }}>Sign Up</div>
        <div className={action === "Sign Up" ? "submit gray" : "submit"} onClick={() => { setAction("Login") }} disabled={!isPasswordValid()}>Login</div>
      </div>
    </div>
  );
};

export default LoginSignUp;
