import React, { useEffect, useState } from 'react'
import './LoginSignUp.css'
 const LoginSignUp = () => {
  const [action,setAction]=useState("Login");
  const [password,setPassword] = useState('');
  const [message,setMessage] = useState('');
  function handleChange(e){
    setPassword(e.target.value)
  }
  const handleValidation = (e) => {
       const regExp = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])[a-zA-Z0-9]{8,}$/
       if (password===""){
        setMessage("choose an password!")
       }else if(regExp.test(password)){
        setMessage("")
       }
       else{
        setMessage("[password is not valid!")
       }
  }
  return (
    <div className='container' >
        <div className='header'>
           <div className='text'>{action}</div>
           <div className='underLine'></div>
        </div>
          <div className='inputs'>
            {action=="Login"? <div></div>: <div className='input'>
                  <img src={require("./icon_email.png")} alt="" />
                  <input type="email" placeholder='Email Id' />
             </div>  }
              <div className='input' onSubmit={handleValidation}>
                  <img src={require("./icon_user.png")} alt="" />
                  <input type="text" placeholder='UserName' />
              </div>
             
             <div className='input'>
                  <img src={require("./icon_pass.png")} alt="" />
                  <input type="password" placeholder='Password' onChange={handleChange} value={password} />
                  <p>{message}</p>
             </div>  
          </div>
          
          <div className="submit-container">
            <div className={action=== "Login"?"submit gray":"submit"} onClick={()=>{setAction("Sign Up")}}>Sign Up</div>
            <div className={action==="Sign Up"?"submit gray":"submit"}onClick={()=>{setAction("Login")}}>Login</div>
          </div>
    </div>
  )
}
export default LoginSignUp
